	sqlStmt+= "  ,CASE WHEN Data_Type__c.Data_Type__c = 'Decimal' THEN  ";
	sqlStmt+= "   CASE WHEN Job_Definition_Template__c.Consider_Min_Value__c = '1' THEN Job_Definition_Template__c.Min_Value__c ";
	sqlStmt+= "   ELSE '0.0' ";
	sqlStmt+= "   END ";
	sqlStmt+= "   ELSE ";
